       const getUserById = async ctx => {
    const { id } = ctx.params;
    try {
      const user = await User.findOne({match:id}).exec();
      if(!user){
        ctx.status = 404;
        return;
      }
      ctx.body = user.toJSON();
    }catch(e){
      ctx.throw(500, e);
    }
  };